export default function Article() {
    // let { id }: { id: string } = useParams();
    let [liked, setliked] = useState(false);
    let [likeSum, setLikeSum] = useState(article.likeNum);
    let handleLike = () => {
        if (!liked) {
            setliked(true);
            setLikeSum(likeSum + 1);
        } else {
            setliked(false);
            setLikeSum(likeSum - 1);
        }
    }
    return (
        <div className="article" dir="rtl">
