                <div className="publication-desc">
                    <RoundedIcone image={me} />
                    <div className="text-desc">
                        <span className="author-name">{article.author}</span>
                        <span className="sub-desc" >פורסם ב-{article.publishDate.toLocaleDateString()}</span>
