import React from 'react';
import { useParams } from 'react-router-dom';
import RoundedIcone from '../components/ect/RoundedIcone';
import me from '../../ressources/img/me.jpeg';
let article = {
    id: 1,
    img: "https://picsum.photos/300/300",
    title: "פוסט נחמד",
    author: "אילן סיאדה",
    desc: "הפוסט הזה נועד לבדיקה.נראה טוב ????",
    publishDate: new Date(),
    commentsNum: 4,
    viewNum: 50,
    likeNum: 2
}
// interface propsArticle {
//     id: number,
//     img: string,
//     title: string,
//     desc: string
//     publishDate: Date,
//     commentsNum: number,
//     viewNum: number,
//     likeNum: number,
//     column?: boolean
// }
export default function Article() {
    let { id }: { id: string } = useParams();
    return (
        <div className="article">
            <div className="head-article">
                <h1>{article.title}</h1>
                <div className="publication-desc">
                    <RoundedIcone image={me} />
                    <div className="text-desc">
                        <span className="author-name"></span>
                    </div>
                </div>
            </div>
        </div>
    )
}
