import React, { useRef } from 'react'
import { Button, TextField } from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';
import axios from 'axios';
export default function MessageForm(props: { action: string }) {
    const name = useRef(null);
    const mail = useRef(null);
    const text = useRef(null);
    let sendComment = () => {
        axios.post('/api/comments/2', {

        })
            .then((res) => {
                console.log(res);
            })
            .catch(function (error) {
                console.log(error);
            })
    };
    return (
        <div className="add-message-form" >
            <TextField ref={name} label="שם מלא" />
            <TextField ref={mail} dir="ltr" label="מייל" />
            <TextField
                ref={text}
                id="standard-textarea"
                label="תגובה"
                rows="4"
                multiline
            />
            <Button
                dir="ltr"
                variant="contained"
                endIcon={<SendIcon />}
                onClick={sendComment}
            >
                שלח
            </Button>
        </div>
    )
}
