import React, { useState } from 'react';

export default (second: number, WrappedComponent: any) => {
    const [isAppear, setIsAppear] = useState(false);
    setTimeout(() => {
        setIsAppear(true);
    }, (second * 1000));

    const displayAfter = ({ ...props }) => <WrappedComponent isAppear={isAppear} {...props} />


    return displayAfter;
}
