import React from 'react';
import '../../../ressources/scss/components/Comments.scss';
import { getFullDate, getFullTime } from '../../../utils/getFullDateAndTime';

export default function Comments(props: { comments: { author: string, date: Date, msg: string } }) {
    let { comments } = props;
    let date = new Date(comments.date);
    return (
        <div className="message anim-opacity">

            <h5>{comments.author} - <span className="sub-desc" > פורסם ב-{getFullDate(date)}<br /> בשעה {getFullTime(date)}  </span></h5>

            <div className="message-content">
                <p>{comments.msg}</p>
            </div>
        </div>
    )
}
