import React, { useEffect } from 'react'
import "../../../ressources/scss/components/Modal.scss";
export default function Modal(props: { openState: boolean, handleClose: any, children: any }) {
    useEffect(() => {
        document.body.classList.toggle("no-scroll");
        return () => {
            document.body.classList.toggle("no-scroll");
        }
    })
    return (
        <React.Fragment>
            {props.openState && <div className="modal" onClick={props.handleClose}>
                {props.children}
            </div>
            }
        </React.Fragment>
    )
}
