let getFullDate = (date: Date): string => {
    return date.getFullYear() + '-' + (date.getMonth() + 1) + '-' + date.getDate();
}
let getFullTime = (date: Date): string => {
    return date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds();
}
export { getFullDate, getFullTime };
