import React, { useRef } from 'react'
import { Button, TextField } from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';
import axios from 'axios';
import isEmail from '../../../../utils/isEmail';
export default function MessageForm(props: { action: string }) {
    const name = useRef(document.createElement("input"));
    const mail = useRef(document.createElement("input"));
    const text = useRef(document.createElement("input"));
    let data = [name, mail, text];
    let sendComment = () => {
        if (isEmail(mail.current.value)) {
            axios.post('/api/comments/2', data)
                .then((res) => {
                    console.log(res);
                })
                .catch(function (error) {
                    console.log(error);
                })
        }

    };
    return (
        <div className="add-message-form" >
            <TextField ref={name} label="שם מלא" />
            <TextField ref={mail} dir="ltr" label="מייל" />
            <TextField
                ref={text}
                id="standard-textarea"
                label="תגובה"
                rows="4"
                multiline
            />
            <Button
                dir="ltr"
                variant="contained"
                endIcon={<SendIcon />}
                onClick={sendComment}
            >
                שלח
            </Button>
        </div>
    )
}
