import React, { useEffect, useState } from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState, convertToRaw } from "draft-js";
import JsxParser from 'react-jsx-parser';
import SendIcon from '@material-ui/icons/Send';
import draftToHtml from 'draftjs-to-html';
import { Button, TextField } from "@material-ui/core";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import "../../ressources/scss/components/AdminSection.scss";
export default function AdminEditorSection() {
    let [preview, setPreview] = useState<any>('');
    const [editorState, setEditorState] = useState(() =>
        EditorState.createEmpty()
    );
    let handlePreview = () => {
        setPreview((draftToHtml(convertToRaw(editorState.getCurrentContent()))));
        console.log(preview)

    }
    useEffect(() => {

    }, [editorState]);

    return (
        <div className="admin-section">
            <h1>Article builder</h1>
            <div className="article-editor">
                <form action="" className="article-editor-form">

                    <TextField id="outlined-basic" label="Title article" variant="outlined" /><br />
                    <div className="upload-image">
                        <p>Image header for the article : </p><br />
                        <Button
                            className="base-color"
                            variant="contained"
                            component="label"
                        >
                            upload image
                    <input
                                id="image"
                                type="file"
                                hidden
                            />
                        </Button>
                    </div>
                    <p>Edit content :</p>
                    <Editor
                        editorState={editorState}
                        onEditorStateChange={setEditorState}
                        onChange={handlePreview}
                    />
                    <div className="final-buttons">
                        <Button
                            className="base-color"
                            type="submit"
                            variant="contained"
                            endIcon={<SendIcon />}
                        >
                            Send
        </Button>
                        <Button className="base-color">Show preview</Button>
                    </div>

                </form>

            </div>

            {/* <div className="display-article">
                <JsxParser
                    jsx={`${preview}`}
                />
            </div> */}
        </div>
    );
}
