import React, { useState } from 'react';
import PropTypes from 'prop-types';
export default (WrappedComponent: any) => {
    const [isAppear, setIsAppear] = useState(false);
    let displayAt = (second: number) => {
        setTimeout(() => {
            setIsAppear(true);
        }, (second * 1000));
    }

    const displayAfter = ({ ...props }) => <WrappedComponent isAppear={isAppear} {...props} />
    displayAfter.propTypes = {
        isAppear: PropTypes.bool
    };

    return displayAfter;
}
