import React, { useRef, useState } from 'react'
import { Button, TextField } from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';
import axios from 'axios';
import isEmail from '../../../../utils/isEmail';
export default function MessageForm(props: { id: string, action: string }) {
    let [sendedComment, setSendcomment] = useState(false);
    const name = useRef(document.createElement("input"));
    const mail = useRef(document.createElement("input"));
    const text = useRef(document.createElement("input"));

    let sendComment = () => {
        let data = {
            name: name.current.value,
            mail: mail.current.value,
            date: new Date(),
            text: text.current.value,
            articleId: props.id
        }
        if (isEmail(data.mail)) {
            axios.post(`${props.action}${props.id}`, data)
                .then((res) => {
                    if (res.data)
                        setSendcomment(true);
                })
                .catch(function (error) {
                    console.log(error);
                })
        }

    };
    return (
        <React.Fragment>
            {!sendedComment ? <div className="add-message-form" >
                <TextField inputRef={name} label="שם מלא" />
                <TextField inputRef={mail} dir="ltr" label="מייל" />
                <TextField
                    inputRef={text}
                    id="standard-textarea"
                    label="תגובה"
                    rows="4"
                    multiline
                />
                <Button
                    dir="ltr"
                    variant="contained"
                    endIcon={<SendIcon />}
                    onClick={sendComment}
                >
                    שלח
        </Button>
            </div>
                :
                <div><p>Send message!</p></div>
            }
        </React.Fragment>
    )
}
