type operator = 'greater' | 'lesser' | 'equal'
let checkStringSize = (string: string, length: number, operator: operator): void => {
    let res = () => {
        switch (operator) {
            case 'greater':
                return string.length > length;
            case 'lesser':
                return string.length < length;
            default:
                return string.length === length;
        };
    }
    console.log(res());
}
export default checkStringSize;
