import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types'
function DisplayAfter(props: { second: number, children: any }) {
    let [isAppear, setIsAppear] = useState(true);
    useEffect(() => {
        setTimeout(() => {
            setIsAppear(true);
        }, (props.second * 1000));
    })
    return (
        <React.Fragment>
            {isAppear && <div>
                {props.children}
            </div>
            }
        </React.Fragment>

    )
}




